def text_to_bits(text, encoding='utf-8', errors='surrogatepass'):
    bits = bin(int.from_bytes(text.encode(encoding, errors), 'big'))[2:]
    return bits.zfill(8 * ((len(bits) + 7) // 8))

def text_from_bits(bits, encoding='utf-8', errors='surrogatepass'):
    n = int(bits, 2)
    return n.to_bytes((n.bit_length() + 7) // 8, 'big').decode(encoding, errors) or '\0'

def popupmsg(msg,size=None):
    print("\nPOPUP:")
    print(msg + "\n")
    
def enc(m,k):
    c = ""
    m = text_to_bits(m)
    for elem in range(len(m)):
        if m[elem] != k[elem] :
            c = c + "1"
        elif m[elem] == k[elem]:
            c = c + "0"
        else:
            print("Error")
            quit()
    return c

def dec(c, k):
    m = ""
    for elem in range(len(c)):
        if c[elem] == "1":
            if k[elem] == "1":
                m = m + "0"
            elif k[elem] == "0":
                m = m + "1"
            else:
                print("Error")
                quit()
        elif c[elem] == "0":
            m = m + k[elem]
        else:
            print("Error")
            quit()
    m = text_from_bits(m)
    return m

def generate_key(length):
    k = ""
    for elem in range(length):
        k = k + str(random.randint(0,1))
    k = text_from_bits(k, "iso8859_2")
    return k
def dec_key(iso):
    k = text_to_bits(iso, "iso8859_2")
    return k
