def senden():
    global UDP_IP, key, eingabe
    wert = "1234567"
    while True:
        #print(UDP_IP)
        nachricht = wert
        nachricht = enc(nachricht, key)
        nachricht = nachricht.encode()
        sock.sendto(nachricht, (UDP_IP, UDP_PORT))
        wert = str(int(wert)+1)
        print("SENT: " + str(wert))
        time.sleep(1)

def empfangen():
    global key
    Verlauf = ''
    time.sleep(1)
    while True:
        data, addr = sock.recvfrom(4096)
        data = data.decode()
        data = str(data)
        data = dec(data,key)#asdfsadfasdf
        Verlauf = Verlauf + '\n' + data
        print("\nVERLAUF:")
        print(Verlauf + "\n")
        time.sleep(0.1)
